export default function PDFShowcase() {
  const [hoveredId, setHoveredId] = useState<string | null>(null)
  const [isLoaded, setIsLoaded] = useState(false)
  const router = useRouter()

  useEffect(() => {
    const timer = setTimeout(() => setIsLoaded(true), 100)
    return () => clearTimeout(timer)
  }, [])

  const handlePDFClick = (url: string) => {
    const encodedUrl = encodeURIComponent(url)
    router.push(`/${encodedUrl}`)
  }

  return (
    <div className="fixed inset-0 overflow-hidden pointer-events-none">
      {/* Floating PDF cards scattered across the background */}
      <div className="absolute inset-0">
        {featuredPDFs.map((pdf, index) => {
          const positions = [
            { top: '5%', left: '3%', rotate: '-12deg' },
            { top: '8%', right: '5%', rotate: '8deg' },
            { top: '45%', left: '2%', rotate: '15deg' },
            { top: '15%', left: '25%', rotate: '-8deg' },
            { top: '75%', left: '4%', rotate: '12deg' },
            { bottom: '15%', right: '3%', rotate: '-15deg' },
            { bottom: '5%', left: '38%', rotate: '6deg' },
            { bottom: '4%', right: '25%', rotate: '-10deg' },
            { top: '12%', right: '20%', rotate: '10deg' },
            { top: '4.5%', left: '45%', rotate: '-12deg' }
          ]
          
          const position = positions[index]
          const isHovered = hoveredId === pdf.id

          return (
            <div
              key={pdf.id}
              className={`absolute pointer-events-auto cursor-pointer transform transition-all duration-500 hover:scale-110 hover:z-50 ${
                isLoaded ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-8'
              }`}
                              style={{
                  ...position,
                  transform: `rotate(${position.rotate}) ${isHovered ? 'scale(1.1)' : 'scale(1)'} ${isLoaded ? 'translateY(0)' : 'translateY(2rem)'}`,
                  zIndex: isHovered ? 50 : 25,
                  transitionDelay: `${index * 100}ms`
                }}
              onMouseEnter={() => setHoveredId(pdf.id)}
              onMouseLeave={() => setHoveredId(null)}
              onClick={() => handlePDFClick(pdf.url)}
            >
              {/* PDF Card */}
              <div className="relative group">
                {/* Main card */}
                <div className="w-32 h-44 bg-white rounded-lg shadow-lg border border-gray-200 overflow-hidden relative">
                  {/* Cover image */}
                  <div className="w-full h-32 bg-gray-100 flex items-center justify-center">
                    <img
                      src={pdf.coverUrl}
                      alt={pdf.title}
                      className="w-full h-full object-cover"
                      onError={(e) => {
                        const target = e.target as HTMLImageElement
                        target.style.display = 'none'
                        target.parentElement!.innerHTML = `
                          <div class="w-full h-full flex items-center justify-center bg-gradient-to-br from-blue-500 to-purple-600 text-white text-xs font-medium p-2 text-center">
                            ${pdf.title}
                          </div>
                        `
                      }}
                    />
                  </div>
                  
                  <div className="p-2">
                    <h3 className="text-xs font-semibold text-gray-900 truncate">{pdf.title}</h3>
                    <p className="text-xs text-gray-600 truncate">{pdf.author}</p>
                  </div>

                  <div className="absolute top-2 left-2">
                    <span className="inline-block px-2 py-1 text-xs font-medium bg-blue-100 text-blue-800 rounded-full">
                      {pdf.category}
                    </span>
                  </div>
                </div>

                {isHovered && (
                  <div className="absolute inset-0 bg-black bg-opacity-80 rounded-lg flex items-center justify-center transition-all duration-200">
                    <div className="text-center text-white p-3">
                      <ExternalLink className="h-6 w-6 mx-auto mb-2" />
                      <p className="text-sm font-medium">Open in Blen</p>
                    </div>
                  </div>
                )}

                {/* Subtle shadow for depth */}
                <div className="absolute inset-0 bg-black opacity-10 rounded-lg transform translate-x-1 translate-y-1 -z-10"></div>
              </div>
            </div>
          )
        })}
      </div>

      {/* Subtle background overlay to ensure readability */}
      <div className="absolute inset-0 bg-white bg-opacity-30 backdrop-blur-sm pointer-events-none -z-10"></div>
    </div>
  )
} 